// arrow function 的 this 指向是定义的时候确定的
// 在绑定定时器/窗口事件的时候不需要 let _this = this

const currentUrl = _ => window.location.href;

const difference = (arr, values) => arr.filter(v => !values.includes(v));

// 如果有多条语句 就要使用{} 包裹，并且增加return 语句
const isEven = num => Math.abs(num) % 2 === 0;

// equal
const isEven = num => { return Math.abs(num) % 2 === 0 }
